import '../styles/App.scss';
import '../styles/core/_reset.scss';
import { useState, useEffect } from 'react';
import api from '../services/charactersApi';
import Header from './Header';
import Footer from './Footer';
import CharacterList from './CharacterList';
import CharacterSearch from './CharacterSearch';

function App() {
  const [data, setData] = useState([]);
  useEffect(() => {
    api.getCharactersFromApi().then((initialData) => {
      setData(initialData);
    });
  }, []);

  return (
    <div className='page'>
      <Header></Header>
      <main>
        <section>
          <CharacterSearch />
          <form className='form__search'>
            <label className='form__search--label' htmlFor=''>
              Â¿Que personaje quieres ver?
            </label>
            <input className='form__search--input' type='text' name='' id='' />
          </form>
        </section>
        <section>
          <CharacterList data={data} />
        </section>
      </main>
      <Footer></Footer>
    </div>
  );
}

export default App;
