function getCharactersFromApi() {
  return fetch(
    '//raw.githubusercontent.com/Adalab/rick-y-morty/master/data/rick-y-morty.json'
  )
    .then((response) => response.json())
    .then((json) => {
      return json.results.map((characters) => {
        return {
          id: character.results.id,
          name: character.results.name,
          specie: character.results.species,
          image: character.results.image,
          origin: character.results.origin.name,
          //episode: character.results.episode,
          status: character.results.status,
        };
      });
    });
}
const objetToExport = {
  getCharactersFromApi: getCharactersFromApi,
};
export default objetToExport;
